div.textarea:
@startuml
Alice -> Bob : Hello
Bob -> Alice : Hi there!
@enduml

body.script:
                            function generateDiagram() {
                                const code = document.getElementById('plantumlCode').value;
                                const resultDiv = document.getElementById('result');

                                fetch('/plantuml/generate', {
                                    method: 'POST',
                                    headers: {
                                        'Content-Type': 'application/x-www-form-urlencoded',
                                    },
                                    body: 'text=' + encodeURIComponent(code)
                                })
                                .then(response => response.text())
                                .then(data => {
                                    resultDiv.innerHTML = '
Generated Diagram:';
                                })
                                .catch(error => {
                                    resultDiv.innerHTML = '
Error: ' + error.message + '';
                                });
                            }
